{% if product.images.size > 0 %}
  <div id="best-seller-badge" class="best-seller-badge" style="position: absolute; top: 15px; left: 15px; z-index: 5; pointer-events: none;">
    <div style="background-color: #F0E7FF; padding: 2px 10px; border-radius: 6px; box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px 0 rgba(0, 0, 0, 0.06); border: 1px solid #342c40;">
      <span style="color: #342c40; font-weight: bold; font-size: 12px;">
        MEILLEUR VENTE
      </span>
    </div>
  </div>

  <style>
    @media (min-width: 768px) {
      .best-seller-badge {
        display: none;
      }
    }
    .product-gallery__media-list-wrapper {
      position: relative;
    }
    .product-gallery__media-list-wrapper .best-seller-badge {
      position: absolute;
      z-index: 5;
    }
  </style>

  <script>
    document.addEventListener('DOMContentLoaded', function() {
      var badge = document.getElementById('best-seller-badge');
      var mediaListWrapper = document.querySelector('.product-gallery__media-list-wrapper');

      if (mediaListWrapper && badge) {
        mediaListWrapper.appendChild(badge);

        var firstImage = mediaListWrapper.querySelector('.product-gallery__media');
        if (firstImage) {
          var observer = new IntersectionObserver(function(entries) {
            entries.forEach(function(entry) {
              badge.style.display = entry.isIntersecting ? 'block' : 'none';
            });
          }, { threshold: 0.5, root: null });

          observer.observe(firstImage);
        }
      }
    });
  </script>
{% endif %}
